import  React, { useState, useMemo } from "react";
import { Outlet } from "react-router-dom";
import Footer from "./Components/Footer";
import Navbar from "./Components/Navbar";
import ThemeContext from "./context";

export const themes = {
  light: {
      font: 'black',
      background: 'white'
  },
  dark: {
      font: 'white',
      background: 'black'
  },
};

function App() {
  const localValue = JSON.parse(localStorage.getItem("theme"));

  const [theme, setTheme] = useState(localValue ?? themes.light);

  // Funcion que altera el estado del tema
  const handleChangeTheme = () => {
    theme === themes.dark ? setTheme(themes.light) : setTheme(themes.dark);
  };

  useEffect(() => {
    localStorage.setItem("theme", JSON.stringify(theme));
  }, [theme]);

  const providerValue = useMemo(()=>({theme,
    handleChangeTheme}),[theme,handleChangeTheme])

  return (
      <div className="App">
        <ThemeContext.Provider value={providerValue}>
          <Navbar/>
          <Outlet/>
          <Footer/>
        </ThemeContext.Provider>
      </div>
  );
}

export default App;
